import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

export interface IUserDto {
  userId: number,
  email: string,
  userTypeName: string
}

export interface userPageData{
  firstName: string;
  middleName: string;
  lastName: string;
  email: string;
  dateOfBirth: string;
  // можно будет дописать - отзывы
}

@Injectable({
  providedIn: 'root'
})

export class UserService {

  isAuthorized = false; // если true - пользователь зашел в аккаунт
  userId = 0;

  setAuthorizedStatus(){
    this.isAuthorized = !this.isAuthorized;
  }

  get authorizedStatus(){
    return this.isAuthorized
  }

  getAllProducts(productType : string): Observable<Product[]> {
    return this.http.get<Product[]>(`${this.apiUrl}/Product/GetAllProducts/${productType}`);
  }

  constructor() { }
}
