import { Component, Input, OnInit} from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { ProductService } from 'src/app/core/services/product.service';
import { FavouriteService } from 'src/app/core/services/favourite.service';
import { UserService } from 'src/app/core/services/user.service';

export interface Product{
  productId: number,
  name: string,
  description: string,
  producrCategoryId: number,
  productTypeId: number,
  price: number,
  images: string[],
  sizes: string[]
  brandTypeId: number,
  genderTypeId: number
}

export interface Filters{
  filterByMinPrice?: number, 
  filterByMaxPrice?: number, 
  filterByGender?: number, 
  filterByName?: string, 
  isFilterByDecreasePrice?: boolean, 
  isFilterByIncreasePrice?: boolean
}

@Component({
  selector: 'app-catalog',
  templateUrl: './catalog.component.html',
  styleUrls: ['./catalog.component.css']
})
export class CatalogComponent implements OnInit{

  constructor(private router: Router, private productService: ProductService, private favouriteService: FavouriteService, private userService: UserService, private route: ActivatedRoute) { }

  products: Product[] = [];
  filters: Filters = {} 
  productType: string = ''
  
  inactiveStar = '../../../../../assets/images/icons/favourites.svg'
  activeStar = '../../../../../assets/images/icons/activeFavourite.svg'
  starStatus = this.inactiveStar;

  ngOnInit() {

    this.route.params.subscribe(params => {
      this.productType = params['item'];
      this.productService.getAllProducts(this.productType).subscribe(
        (productsFromQuery: Product[]) => {
          this.products = productsFromQuery;
        },
        (error) => {
          console.error('Error fetching products', error);
        }
      );
    });
  }

  public addToFavourite(product: Product, star: HTMLImageElement) {
    if(!this.userService.isAuthorized)
      return;
    
    if(star.src.includes('activeFavourite')){
      star.src = this.inactiveStar;
      this.favouriteService.DeleteProductFromFavourites(product.productId, this.userService.userId);
    }
    else {
      star.src = this.activeStar;
      this.favouriteService.AddFavouriteProduct(product.productId, this.userService.userId);
    }
  }

  getFilteredProducts(filters: Filters){

    this.productService.getFilteredProducts(filters, this.productType).subscribe(
      (productsFromQuery: Product[]) => {
        this.products = productsFromQuery;
      },
      (error) => {
        console.error('Error fetching products', error);
      }
    );
  }

  goToProduct(id: number) {
    this.router.navigate(['catalog/product', id]);
  }

  getFormatPrice(price: number){
    return (this.productService.getFormattedPrice(price))
  }

}
<app-header></app-header>

<div class="settings-bar"> 
  <div>Сортировка</div>
</div>

<div class="container">

  <div class="optional-menu">
    <input class="optional__search" [(ngModel)]="filters.filterByName" placeholder="Найти" type="text">

    <div class="price-filter">
      <p class="filter-heading">Cортировать по цене:</p>
      <input class="optional__price left" [(ngModel)]="filters.filterByMinPrice" placeholder="Цена от" type="number">
      <input class="optional__price" [(ngModel)]="filters.filterByMaxPrice" placeholder="Цена до" type="number">
    </div>

    <div class="sex-filter__container">
      <p class="filter-heading">Сортировать по полу:</p>
      <ul class="sex-filter">
        <li class="sex-filter__item">Мужской</li>
        <li class="sex-filter__item">Женский</li> 
        <li class="sex-filter__item">Унисекс</li>
      </ul>
    </div>

    <div class="clear-section">
      <input type="checkbox" name="clear" id="">
      <label for="clear">Очистить всё</label>
    </div>
    
    <button (click)="getFilteredProducts(filters)">Покажи</button>
  </div>

  <div class="product" *ngFor="let product of products">
    <img class="star" (click)="addToFavourite(product, star)" [src]="inactiveStar" #star>
    <div class="product-image">
      <app-carousel [images]="product.images"></app-carousel>
    </div>
    <div class="product-description" (click)="goToProduct(product.productId)">
      <h2 class="product-name">{{product.name}}</h2>
      <p class="product-price">{{getFormatPrice(product.price)}}</p>
    </div>
  </div>
</div>
<app-footer></app-footer>
