import { Component } from '@angular/core';
import { AuthorizationService } from 'src/app/core/services/authorization.service';
import { ChangeDetectorRef } from '@angular/core';
import { UserService } from 'src/app/core/services/user.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  constructor(private changeDetector: ChangeDetectorRef ,private registrationService: AuthorizationService, private userService: UserService, private router: Router) { }

  isAuthorize = this.userService.isAuthorized

  openAuthWindow() {
    if(this.userService.authorizedStatus){
      this.router.navigate(['/profile']);
    } else{
      this.registrationService.setAuthWindowStatus();
    }
  }

  routeToFavourites() {
    if(this.userService.authorizedStatus){
      this.router.navigate(['/favourites']);
    } else{
      this.registrationService.setAuthWindowStatus();
    }
  }

  routeToShoppingCart() {
    if(this.userService.authorizedStatus){
      this.router.navigate(['/cart']);
    } else{
      this.registrationService.setAuthWindowStatus();
    }
  }
}
<header>
    <div class="container">
      <a routerLink="">
        <div class="header_logo">
          SATCHEL
        </div>
      </a>
  
      <nav class="header_navigation">
        <ul class="header_nav-list">
          <li class="header_nav-list__item"><a routerLink="">Информация</a></li>
          <li class="header_nav-list__item"><a [routerLink]="['/catalog', 'clothes']">Одежда</a></li>
          <li class="header_nav-list__item"><a [routerLink]="['/catalog', 'shoes']">Обувь</a></li>
          <li class="header_nav-list__item"><a [routerLink]="['/catalog', 'accessories']">Аксессуары</a></li>  
        </ul>
      </nav>
  
      <div class="header_icons">
        <a class="header_icons__item" (click)="routeToFavourites()"><img src="./assets/images/icons/favourites.svg" alt="Favourites"></a>
        <a class="header_icons__item" (click)="routeToShoppingCart()"><img src="./assets/images/icons/bag.svg" alt="Cart"></a>
        <a class="header_icons__item" (click)="openAuthWindow()"><img src="./assets/images/icons/personalArea.svg" alt="My Account"></a>
      </div>

    </div>
</header>
