import { Component, OnInit, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductService } from 'src/app/core/services/product.service';
import { Product } from '../catalog/catalog.component';
import { CartPageService } from 'src/app/core/services/cart-page.service';
import { UserService } from 'src/app/core/services/user.service';
import { AuthorizationService } from 'src/app/core/services/authorization.service';
import { ConfigService } from 'src/app/core/services/config.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent implements OnInit {

  id = 0;
  selectedSize: string | null = null;
  statusMsg = ''

  @Input() product: Product = {
    productId: 0,   
    name: '',
    description: '',
    producrCategoryId: 0,
    productTypeId: 0,
    price: 0,
    images: [''],
    sizes: [''],
    brandTypeId: 0,
    genderTypeId: 0,
    sizeTypeId: 0
  }

  constructor( private route: ActivatedRoute, 
               private productService: ProductService, 
               private cartPageService: CartPageService, 
               private userService: UserService, 
               private registrationService: AuthorizationService,
               private configService: ConfigService) { }

  ngOnInit() {
    const id = this.route.snapshot.paramMap.get('id');
    this.id = id ? +id : 0;
    
    this.getProductById();
  }

  onSizeClick(size: string) {
    if (size === this.selectedSize) {
      this.selectedSize = null;  
    } else {
      this.selectedSize = size;
    }
  }

  addProductToShoppingCart(){
    if(!this.userService.authorizedStatus){
      this.registrationService.setAuthWindowStatus();
      return 
    } 
    if (this.selectedSize != null && this.userService.userId != 0){
      this.cartPageService.AddProductToShoppingCart(this.id, this.userService.userId, this.selectedSize).subscribe(
        () => {
          this.statusMsg = 'Товар успешно добавлен!';
        },
        (error) => {
          this.statusMsg = 'Что-то пошло не так!';
        }
      );

      setTimeout(() => {
        this.statusMsg = ''
      }, 2000)

      this.selectedSize = null
    }
  }

  getFormatPrice(price: number){
    return (this.configService.getFormattedPrice(price))
  }

  getProductById() : void {
    this.productService.getProductById(this.id).subscribe(
      (product) => {
        this.product = product;
        console.log(product)
      },
      (error) => {
        console.error('Error fetching product by ID:', error);
      }
    );
  }
}
<app-header></app-header>

<div class="container">
    <div class="product_gallery">
        <div class="image-container">
            <app-carousel class="product-carousel" [images]="product.images"></app-carousel>
        </div>
    </div>

    <div>
        <div class="product_info">
            <p class="product_name">{{product.name}}</p>
            <p class="product_price">{{getFormatPrice(product.price)}}</p>
    
            <ul class="sizes-container">
                <li class="size-box" *ngFor="let size of product.sizes"
                    [class.selected]="size === selectedSize"
                    (click)="onSizeClick(size)"
                > 
                    {{size}}
                </li>
            </ul>
    
            <button class="buy-btn" (click)="addProductToShoppingCart()"> Добавить в корзину</button>

        </div>
    
        <div class="product__description">
            {{product.description}}
        </div>
    </div>

    <div *ngIf="statusMsg" class="statusMessage">{{statusMsg}}</div>
</div>
