import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

export interface IUserDto {
  userId: number,
  email: string,
  userTypeName: string
}

@Injectable({
  providedIn: 'root'
})

export class UserService {

  isAuthorized = false; // если true - пользователь зашел в аккаунт
  userId = 0;

  private authorizedStatusSubject = new BehaviorSubject<boolean>(false);
  authorizedStatus$ = this.authorizedStatusSubject.asObservable();

  setAuthorizedStatus(status: boolean): void {
    this.authorizedStatusSubject.next(status);
  }

  setAuthorizedStatus(){
    this.isAuthorized = !this.isAuthorized;
  }

  get authorizedStatus(){
    return this.isAuthorized
  }

  constructor() { }
}
