import { Component, Input, OnInit} from '@angular/core';
import { Router } from '@angular/router';
import { ProductService } from 'src/app/core/services/product.service';

export default interface Product{
  id: number,
  name: string,
  description: string,
  producrCategoryId: number,
  productTypeId: number,
  price: number,
  images: string[],
  sizes: string[]
  brandTypeId: number,
  genderTypeId: number
}

@Component({
  selector: 'app-catalog',
  templateUrl: './catalog.component.html',
  styleUrls: ['./catalog.component.css']
})
export class CatalogComponent implements OnInit{

  constructor(private router: Router, private productService: ProductService) { }

  products : Product[] = [];

  ngOnInit() {
    this.productService.getAllClothes().subscribe(
      (productsFromQuery: Product[]) => {
        this.products = productsFromQuery;
        console.log(this.products[0].images);
      },
      (error) => {
        console.error('Error fetching products', error);
      }
    );
  }

  goToProduct(id: number) {
    this.router.navigate(['catalog/product', id]);
  }

  formatNumber(num: number) {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  }
  
  getFormattedPrice(price: number) {
    return `${this.formatNumber(price)} ₽`; 
  }
}
<app-header></app-header>

<div class="settings-bar"> 
    <div>Фильтры</div>
    <div>Сортировка</div>
</div>

<div class="container">
    <div class="product" *ngFor="let product of products">
      <img class="star" ngClass="active" src="" alt="">
      <div class="product-image">
        <app-carousel [images]="product.images"></app-carousel>
      </div>
      <div class="product-description" (click)="goToProduct(product.id)">
        <h2 class="product-name">{{product.name}}</h2>
        <p class="product-price">{{getFormattedPrice(product.price)}}</p>
      </div>
    </div>
</div>
<app-footer></app-footer>
