import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import { HttpClient } from '@angular/common/http';

export interface Step {
  title: string;
  fields: {
    name: string;
    type: string;
  }[] 
}

@Injectable({
  providedIn: 'root'
})
export class AuthorizationService {

  constructor(private http: HttpClient) {}

  private apiUrl = 'https://localhost:7082/api/Product';

  stepChange = new Subject();
  currentStep = 0;
  isRegistrationOpen : boolean = false;

  steps: Step[] = [
    {
      title: 'Введите email',
      fields: [
        {name: 'email', type: 'email'}  
      ]
    },
    {
      title: 'Введите пароль',
      fields: [
        {name: 'password', type: 'password'} 
      ]
    },
    {
      title: 'Регистрация',
      fields: [
        {name: 'email', type: 'email'},
        {name: 'password', type: 'password'},
        {name: 'name', type: 'name'}
      ]
    } 
  ]

  get step() {
    return this.steps[this.currentStep]; 
  }

  login(email?: string, password?: string) {
    this.currentStep = 1; 
    this.stepChange.next(this.currentStep); 

    if (email && password)
    {
      const user = {
        email: email,
        password: password,
        userType: 0
      }
      console.log(user);
      this.http.post(`${this.apiUrl}/CreateUser`, user);
    }
  }

  prev() {
    this.currentStep--;
  }  

  openAuthWindow() : void {
    this.isRegistrationOpen = true;
  }

  closeAuthWindow() : void{
    this.isRegistrationOpen = false;
  }
}
