import { Injectable } from '@angular/core';
import { Subject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

export interface Step {
  title: string;
  fields: {
    name: string;
    type: string;
  }[] 
}

export interface IUser {
  email : string;
  password: string;
  userType: number;
}

@Injectable({
  providedIn: 'root'
})
export class AuthorizationService {

  constructor(private http: HttpClient) {}

  private apiUrl = 'https://localhost:7082/api';

  stepChange = new Subject();
  currentStep = 0;
  isRegistrationOpen : boolean = false;

  steps: Step[] = [
    {
      title: 'Введите email',
      fields: [
        {name: 'email', type: 'email'}  
      ]
    },
    {
      title: 'Введите пароль',
      fields: [
        {name: 'password', type: 'password'} 
      ]
    },
    {
      title: 'Регистрация',
      fields: [
        {name: 'email', type: 'email'},
        {name: 'password', type: 'password'},
        {name: 'name', type: 'name'}
      ]
    } 
  ]

  get step() {
    return this.steps[this.currentStep]; 
  }

  login(email?: string, password?: string): Observable<IUser>{
    this.currentStep = 1; 
    this.stepChange.next(this.currentStep); 

    if (email && password)
    {
      return this.addNewUser(email, password).subscribe(response => {
        console.log(response);
        // Обработайте ответ от сервера
      }, error => {
        console.log(error);
        // Обработайте ошибку
      });
    }
  }

  logi12n(email?: string, password?: string) {
    this.currentStep = 1; 
    this.stepChange.next(this.currentStep); 
  
    if (email && password)
    {
      return this.addNewUser(email, password);
    }
  }

  goToPreviousStep() {
    this.currentStep--;
  }  

  openAuthWindow() : void {
    this.isRegistrationOpen = true;
  }

  closeAuthWindow() : void{
    this.isRegistrationOpen = false;
  }

  addNewUser(email: string, password: string): Observable<IUser> {
    const user = {
      email: email,
      password: password,
      userTypeId: 1
    };
    return this.http.post<IUser>(this.apiUrl + '/CreateUser', user);
  }
}
